mapper.insert:
INSERT INTO user (
    username, password, email, phone, real_name, role, status, create_time, update_time
) VALUES (
    #{username}, #{password}, #{email}, #{phone}, #{realName}, 
    #{role}, #{status}, #{createTime}, #{updateTime}
